import React, { Component } from 'react';
import MenuItem from '../menu-item/menu-item.component';

import './directory.styles.scss'

class Directory extends Component {
    constructor(){
        super();

        this.state = {
            sections: [{
                title: 'hats',
                imageUrl: 'https://i.ibb.co/cvpntL1/hats.png',
                id: 1
            },
            {
                title: 'jackets',
                imageUrl: 'https://i.ibb.co/cvpntL1/jackets.png',
                id: 2
            },
            {
                title: 'sneakers',
                imageUrl: 'https://i.ibb.co/cvpntL1/sneakers.png',
                id: 3
            },
            {
                title: 'mens',
                imageUrl: 'https://i.ibb.co/cvpntL1/mens.png',
                id: 4
            },
            {
                title: 'womens',
                imageUrl: 'https://i.ibb.co/cvpntL1/womens.png',
                id: 5
            }
        ]
        }
    }

    render () {
        return (
            <div className='directory-menu'>
                {this.state.sections.map(item => (<MenuItem key={item.id} item={item}/>))}
                {/* <MenuItem title='HATS'/>
                <MenuItem title='JACKETS'/>
                <MenuItem title='SNEAKERS'/>
                <MenuItem title='MENS'/>
                <MenuItem title='WOMENS'/> */}
            </div>
        );
    };

}

export default Directory;
